import { createSlice } from "@reduxjs/toolkit";


const filterSlice = createSlice({
    name: "filter",
    initialState: [],
    reducers:{
        best: (state, action) =>{
            return action.payload;
        }
    }
})

export default filterSlice.reducer;
export const {best} = filterSlice.actions;
